ALTER TABLE usda_plants ADD fs_Uses TEXT;
UPDATE usda_plants SET fs_Uses='Field brome is used primarily as a winter cover crop 
on vegetable lands and in orchards in the Northeast.  
Due to its heavy root growth and low, dense top 
growth, it gives good protection against wind and 
water erosion.  The heavy root growth makes it a 
useful green manure crop.  When used as a cover 
plant, it provides good pasturage for Canada geese, 
especially in the spring. 
 
 
' WHERE symbol='brar5';
