ALTER TABLE usda_plants ADD fs_Uses TEXT;
UPDATE usda_plants SET fs_Uses='This nitrogen-fixing plant is used primarily as a 
ground cover for erosion control and species diversity 
in sterile, sandy soils.  The abundant underground 
stems, or rhizomes, makes it especially suited to 
stabilizing steep, sandy banks.  It makes a pleasing 
companion plant in a coastal setting with the low 
growing junipers, rugosa rose, bayberry, and beach 
plum. 
' WHERE symbol='cope80';
ALTER TABLE usda_plants ADD fs_Status TEXT;
UPDATE usda_plants SET fs_Status='Please consult the PLANTS Web site and your State 
