ALTER TABLE usda_plants ADD fs_Uses TEXT;
UPDATE usda_plants SET fs_Uses='Perennial pea is a long-lived erosion control plant 
that provides a low maintenance and attractive cover 
in highly erodible areas. It requires little input once 
established.  Perennial pea is also useful as a wildlife 
cover plant. When planted with grasses and other 
legumes, it will inhibit the ability of other plants to 
invade the stand after it achieves full cover, and can 
be used to control woody plant encroachment of 
utility rights-of-way.  Perennial pea can be 
overseeded on rip-rap to hide the bare rock. 
' WHERE symbol='lala4';
