ALTER TABLE usda_plants ADD fs_Uses TEXT;
UPDATE usda_plants SET fs_Uses='Pitcher sage is a readily eaten forb, which is 
especially sought out by livestock in the early spring 
when protein levels are high and palatability is good. 
Pitcher sage numbers will decrease in native pastures 
that are over grazed or over stocked with livestock. 
The flowers, which are deep blue and attractive to 
pollinators such as bumble bees and hummingbirds, 
have a persistent, long bloom period late into the fall 
when little else is blooming. The plants are also 
attractive to migrating monarch butterflies and other 
insects.  
  
' WHERE symbol='saazg';
