ALTER TABLE usda_plants ADD fs_Uses TEXT;
UPDATE usda_plants SET fs_Uses='Erosion control:  Sandbar willow is used for 
streambank and lake shore stabilization and riparian 
area development or restoration.  It is recommended 
for deep wet lowland, overflow areas, wet meadow 
sites, streambanks, lake shores, and other areas with a 
high water table. 
Wildlife:  This plant is provides wood and shelter for 
many game birds and forage for deer.' WHERE symbol='saex';
