UPDATE usda_plants SET fs_Uses='This plant is used mainly for landscape 
beautification.  It has potential for use in cultivated 
garden situations, in naturalized prairie or meadow 
plantings, and along roadsides. 
 
 
' WHERE symbol='saly2';
