ALTER TABLE usda_plants ADD fs_Management_ TEXT;
UPDATE usda_plants SET fs_Management_='Nursery plantings of red elderberry 
can be as dense as 700 plants per acre in soil at least 
24 in. deep.  Consider supplemental irrigation during 
establishment year or years with low rainfall.  Red 
elderberry will re-sprout from both roots and the seed 
bank following fire. Severe pruning will prevent a 
spindly growth habit in ornamental applications. 
 
' WHERE symbol='sara2';
