UPDATE usda_plants SET fs_Uses='Although slender little bluestem is not one of the 
preferred forage grasses in the southern U.S., cattle 
graze it when it is young and tender.   
 
' WHERE symbol='scte5';
ALTER TABLE usda_plants ADD fs_Status TEXT;
UPDATE usda_plants SET fs_Status='Please consult the PLANTS Web site and your State 
