ALTER TABLE usda_plants ADD fs_ TEXT;
ALTER TABLE usda_plants ADD fs_Description TEXT;
UPDATE usda_plants SET fs_Description='Vicia villosa Roth, hairy vetch, is a vining, winter-
active legume.  It may be annual or biennial.  It has a 
shallow root system.  Stems may grow 2 to 5 feet 
long.  Leaves bearing several leaflets grow 
perpendicular to the stem, and may terminate in 
tendrils.  Stems and leaves of hairy vetch are 
pubescent (covered with a soft woolly fuzz) and tufts 
are present at the tips of stems.  Flowers are purple.  
Seeds are round and black, and develop inside 
